													<a href="#" title="#" className="add-butn more-action" data-ripple onClick={() => removeFriend(user.id, userM.id)}>unfriend</a>
													<p>Already a friend</p>
												</>
										}
										{
											(!following.some(el => el.id === userM.id)) ?
